async function carregarComponente(e,r){try{let n=await fetch(e),o=await n.text();document.querySelector(r).innerHTML=o}catch(t){console.error("Erro ao carregar componente:",t)}}document.addEventListener("DOMContentLoaded",()=>{carregarComponente("components/cabecalho.html","#header-container"),carregarComponente("components/rodape.html","#footer-container")});